mapper.select:
select
rec_id, SP_CODE, IS_SENDER, RESOURCE_TYPE, CYCLE_TYPE, BILL_CYCLE, SUM_NUM, COST_FEE,
CREATE_DATE, ADJUST_NUM, OP_CODE, STATUS
from sp_billacct where status=1

  and sp_code = #{spCode}


  and bill_cycle = #{billCycle}

mapper.insert:
insert into sp_billacct (rec_id, SP_CODE, IS_SENDER, 
  RESOURCE_TYPE, CYCLE_TYPE, BILL_CYCLE, 
  SUM_NUM, COST_FEE, CREATE_DATE, 
  ADJUST_NUM, OP_CODE, STATUS
  )
values (#{recId,jdbcType=INTEGER}, #{spCode,jdbcType=VARCHAR}, #{isSender,jdbcType=INTEGER}, 
  #{resourceType,jdbcType=INTEGER}, #{cycleType,jdbcType=VARCHAR}, #{billCycle,jdbcType=VARCHAR},
  #{sumNum,jdbcType=INTEGER}, #{costFee,jdbcType=INTEGER}, #{createDate,jdbcType=TIMESTAMP}, 
  #{adjustNum,jdbcType=INTEGER}, #{opCode,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}
  )
