mapper.select:
select distinct
t.REC_ID, t.TT_CODE, t.SRC_CHANNEL,t.TT_NAME, t.TT_CONTENT, t.TT_ATTACH, t.OPERATOR_CODE, t.RESULT_MSG, t.ALERT_DATE,
t.CREATE_DATE, t.OP_ID, t.ORG_ID, t.DONE_CODE, t.DONE_DATE, t.REMARK, t.STATE,t.LIMIT_UNIT,t.LIMIT_NUM,t.IS_ALERT,t.ALERT_CHANNEL,o.login_code
from ms_tt t left join sec_operator o on (t.OPERATOR_CODE=o.rec_id)
where t.REC_ID = #{recId,jdbcType=INTEGER}

select distinct
t.REC_ID, t.TT_CODE, t.SRC_CHANNEL,t.TT_NAME, t.TT_CONTENT, t.TT_ATTACH, t.OPERATOR_CODE, t.RESULT_MSG, t.ALERT_DATE,
t.CREATE_DATE, t.OP_ID, t.ORG_ID, t.DONE_CODE, t.DONE_DATE, t.REMARK, t.STATE,t.LIMIT_UNIT,t.LIMIT_NUM,t.IS_ALERT,t.ALERT_CHANNEL,o.login_code
from ms_tt t left join sec_operator o on (t.OPERATOR_CODE=o.rec_id)


    and t.tt_code=#{ttCode}


    and t.src_channel=#{srcChannel}


    and t.OPERATOR_CODE=#{operatorCode}


    and t.TT_NAME=#{ttName}


    and date_format(t.alert_date,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


    and date_format(t.alert_date,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
